import React, {useEffect, useState} from "react"
import './App.css';
import {Link} from "react-router-dom";
const axios = require("axios").default;
// axios.defaults.proxy.host = "http://localhost:5000";



function Nav() {
  
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
  
    // Note: the empty deps array [] means
    // this useEffect will run once
    // similar to componentDidMount()
    useEffect(() => {
        let mounted = true;
        console.log("GOT HERE")
        axios.get("/api/getinfo")
        .then(response => {
          // handle success
            if(mounted){
              console.log("GOT HERE II")
              console.log(response.data.customers);
              setIsLoaded(true);
              
              setItems(response.data.customers);
              }
        })
        .catch(error => {
          // handle error
          setError(error);
          setIsLoaded(true);
          console.log(error);
        })
        return () => mounted = false;
    }, [])
  
    var gabriel, esteban, maria;
    [gabriel, esteban, maria] = items
    console.log("LOGGING ITEMS")
    console.log(items)
    if (error) {
      return <div>Error: {error.toString()}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <nav className="nav-bar">
          <h3>Logo</h3>
          <ul className="nav-links">
            <Link to='/'><li>{maria.lName}</li></Link>
            <Link to='/shop'><li>Shop</li></Link>
            <Link to='/about'><li>About</li></Link>
          </ul>
        </nav>
      );
    }
}


export default Nav;
