import React, {useEffect, useState} from "react"
import '../App.css';
import {Link} from "react-router-dom";
import logo from "../img/Logo.jpg"
import Button from '@material-ui/core/Button';
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
const axios = require("axios").default;

const useStyles = makeStyles({
  root: {
    fontSize : "20px",
    textTransform : "none"
  }
});

function Nav() {
    const classes = useStyles();

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
  
    useEffect(() => {
        let mounted = true;
        axios.get("/api/getinfo")
        .then(response => {
          // handle success
            if(mounted){
              setItems(response.data.customers);
              setIsLoaded(true);
              }
        })
        .catch(error => {
          // handle error
          setError(error);
          setIsLoaded(true);
          console.log(error);
        })
        return () => mounted = false;
    }, [])

    var gabriel
    [gabriel] = items

    if (error) {
      return <div>Error: {error.toString()}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <nav className="nav-bar">
          <img src={logo} className="logo" alt="punny pachyderm logo" />
          <ul className="nav-links">
          <Grid container spacing={3}>
          <Grid item sm={4}>
            <Button className={classes.root} component={Link} to="/">Home</Button>
            </Grid>
            <Grid item sm={4}>
            <Button className={classes.root} component={Link} to="/shop">Shop</Button>
            </Grid>
            <Grid item sm={4}>
            <Button className={classes.root} component={Link} to="/About">About</Button>
            </Grid>
            </Grid>
          </ul>
        </nav>
      );
    }
}


export default Nav;
