import React, {useEffect, useState} from "react"
import '../App.css';
import {Link} from "react-router-dom";
import logo from "../img/Logo.jpg"
import Button from '@material-ui/core/Button';
import { makeStyles } from "@material-ui/core/styles";
const axios = require("axios").default;

const useStyles = makeStyles({
  root: {
    fontSize : "25px",
    textTransform : "none"
  }
});

function Nav() {
    const classes = useStyles();

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
  
    useEffect(() => {
        let mounted = true;
        axios.get("/api/getinfo")
        .then(response => {
          // handle success
            if(mounted){
              setItems(response.data.customers);
              setIsLoaded(true);
              }
        })
        .catch(error => {
          // handle error
          setError(error);
          setIsLoaded(true);
          console.log(error);
        })
        return () => mounted = false;
    }, [])

    var gabriel
    [gabriel] = items

    if (error) {
      return <div>Error: {error.toString()}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <nav className="nav-bar" contentEditable="false">
          <img src={logo} className="logo" alt="punny pachyderm logo" />
          <ul className="nav-links">
            <Link to='/' style={{ textDecoration: 'none' }}><li>{gabriel.lName} </li></Link>
            <Button className={classes.root} component={Link} to="/shop">Shop</Button>
            <Link to='/about' style={{ textDecoration: 'none' }}><li>About</li></Link>
          </ul>
        </nav>
      );
    }
}


export default Nav;
